import React from "react";
import "./Header.css";

const nav_links = [
    {
        Path:'#home',
        display:'Home'
    },
    {
        Path:'#about',
        display:'About'
    },
    {
        Path:'#service',
        display:'Service'
    },
    {
        Path:'#projects',
        display:'Projects'
    },
    {
        Path:'#blog',
        display:'Blog'
    },
]

const Header = ({theme, toggleTheme}) => {
    return (
        <header className="header">
            <div className="container">
                <div className="nav_wrapper">
                    <div className="logo">
                        <h2>Iyyappan</h2>
                        
                    </div>
                    {/*==== navigation ==== */}
                    <div className="navigation">
                        <ul className="menu">
                            {nav_links.map((item, index) => (
                                    <li className="menu_item">
                                        <a href={item.Path} className="menu_link">{item.display}</a>
                                    </li>
                                ))}

                        </ul>
                    </div>

                    {/* ==== light mode ==== */}
                    <div className="light_mode">
                        <span onClick={toggleTheme} >
                            {
                                theme === 'light-theme' ? (
                                    <span> 
                                        <i class = 'ri-moon-line'></i>Dark 
                                        </span> 
                                        ) : (
                                            
                                            <span>
                                                <i class='ri-sun-line'></i> Light
                                            </span>

                                        )
                            } 
                            
                        </span>
                    </div>
                </div>
            </div>
        </header>
    )
}

export default Header;
