import React, {useRef, useEffect} from "react";
import "./Header.css";

const nav_links = [
    {
        Path:'#home',
        display:'Home'
    },
    {
        Path:'#about',
        display:'About'
    },
    {
        Path:'#service',
        display:'Service'
    },
    {
        Path:'#projects',
        display:'Projects'
    },
    {
        Path:'#blog',
        display:'Blog'
    },
];

const Header = ({theme, toggleTheme}) => {

    const headerRef = useRef (null);

    const menuRef = useRef(NULL)

    const headerFunc = () => {
        if (document.body.scrollTop > 80 || document.documentElement.scrollTop>80 ){
                headerRef.current.classList.add('header_shrink')
            } else {
                headerRef.current.classList.remove('header_shrink')
            }
    }

    useEffect (() => {
        window.addEventListener('scroll',headerFunc)
        return () => window.removeEventListener('scroll',headerFunc)
    },[])
    const handleClick = (e) => {
    e.preventDefault();

    const targetAttr = e.target.getAttribute("href");
    if (!targetAttr) return;

    const targetEl = document.querySelector(targetAttr);
    if (!targetEl) return;

    const location = targetEl.offsetTop;

    window.scrollTo({
        left: 0,
        top: location - 80,
        behavior: "smooth",
    });
    };

    const toggleMenu = () => menuRef .current.classList.toggle('menu_active');
    return (
        <header className="header" ref={headerRef} > 
            <div className="container">
                <div className="nav_wrapper">
                    <div className="logo">
                        <h2>Iyyappan</h2>
                        
                    </div>
                    {/*==== navigation ==== */}
                    <div className="navigation" ref={menuRef} onClick={toggleMenu}>
                        <ul className="menu">
                            {nav_links.map((item, index) => (
                                    <li className="menu_item" key={index}>
                                        <a href={item.Path} onClick={handleClick} className="menu_link">{item.display}</a>
                                    </li>
                                ))}

                        </ul>
                    </div>

                    {/* ==== light mode ==== */}
                    <div className="light_mode">
                        <span onClick={toggleTheme} >
                            {
                                theme === 'light-theme' ? (
                                    <span> 
                                        <i class = 'ri-moon-line'></i>Dark 
                                        </span> 
                                        ) : (
                                            
                                            <span>
                                                <i class='ri-sun-line'></i> Light
                                            </span>

                                        )
                            } 
                            
                        </span>
                    </div>
                    <span className="mobile_menu" onClick={toggleMenu}>
                        <i class="ri-menu-line"></i>
                    </span>
                </div>
            </div>
        </header>
    )
}

export default Header;
