import React from 'react';
import '../Styles/Testimonial.css';
import Slider from 'react-slick';
import ava01 from '../../images/ava-1.jpg'


const Testimonial = () => {
  return (
    <section>
        <Slider>
            <div className="slide_item">
                <p className="description">
                    Had a good experience in working with their team for my needs in digital marketing services. Their staffs were professional and client relationship was good. Worth the money we spend.
                </p>
                <div className="customer_details">
                    <div className="customer img">
                        <img src= {ava01} alt="" />
                    </div>
                    <div>
                        <h5 className="customer_name">Hari</h5>
                    </div>
                </div>
            </div>
        </Slider>
    </section>
  );
};

export default Testimonial;
